import React from 'react';
import Item from '../item/item.js'
// import ReactDOM from 'react-dom';
// import * as serviceWorker from './serviceWorker';

class List extends React.Component {
    // stores.data.map(item => <Item key = {item.StoreID} storename = {item.StoreName}/>)
    constructor () {
        super()
        this.state = {
            items: [],
            isLoaded: false,
            refresh: false,
            pagination: [],
            page_num: 0
        }
    }
    componentDidMount() {
        // console.log(this.context)
        fetch("http://localhost:3001/stores?page="+this.state.page_num)
        .then(res => res.json())
        .then(json => {
            this.setState({
                isLoaded: true,
                items: json.data,
                pagination: json.pagination
            })
        })
    }
    componentDidUpdate(prevProps) {
        const { filter, refresh, search} = this.props;
        console.log(refresh)
        console.log(prevProps.refresh)
        if (prevProps.refresh !== refresh) {
            var basic_query = "http://localhost:3001/stores?page="
            for (var i in Object.keys(filter)) {
                if (filter[Object.keys(filter)[i]]!=='')
                    basic_query+="&"+Object.keys(filter)[i]+"="+filter[Object.keys(filter)[i]]
            }


            if (search!=='')
                basic_query+="&search_text="+search

            console.log(basic_query)
                fetch(basic_query)
                .then(res => res.json())
                .then(json => {
                    this.setState({
                        isLoaded: true,
                        items: json.data,
                        pagination: json.pagination
                    })
            })   
        } 


      }

    render() {
            var { isLoaded, items, pagination } = this.state
            console.log(pagination)
            if (!isLoaded) {
                return <div>Loading...</div>
            }
            else if (items.length === 0)
                return (
                <div className={"col-sm-9 col-md-6 col-lg-8"}>
                <div className="container-fluid">
                    <p>No Results!</p>
                </div>
                </div>
                )
            else if (typeof pagination.next != 'undefined'){
                return (
                    <div className={"col-sm-9 col-md-6 col-lg-8"}>
                        <div className="container-fluid">
                            {items.map(item => 
                            <Item key = {item.StoreID}
                                  id = {item.StoreID}
                                  address = {item.Address}
                                  storename = {item.StoreName} 
                                  website = {item.Website}
                                  />)}
                        </div>
                        <br />

                        <ul className="pager">
                                <li className="previous"><a href="#">Previous</a></li>
                                <li className="next"><a href="#">Next</a></li>
                        </ul>
                    </div>
                )
            }
            else {
                return (
                    <div className={"col-sm-9 col-md-6 col-lg-8"}>
                        <div className="container-fluid">
                            {items.map(item => 
                            <Item key = {item.StoreID}
                                  id = {item.StoreID}
                                  address = {item.Address}
                                  storename = {item.StoreName} 
                                  website = {item.Website}
                                  />)}
                        </div>
                        <br />
                    </div>
                )
            }
    }
}


export default List;

// If you want your app to work offline and load faster, you can change
