import React from 'react';
import Option from '../option/option.js'
import Data from './dropdownData.js'
// import ReactDOM from 'react-dom';
// import * as serviceWorker from './serviceWorker';

class Dropdown extends React.Component {

    // themeComponents = Data.themeTypeData.map(option => <Option key = {option.id} name = {option.name}/>)
    // neighborhoodComponents = Data.neighborhoodTypeData.map(option => <Option key = {option.id} name = {option.name}/>)
    // forwhomComponents = Data.forwhomTypeData.map(option => <Option key = {option.id} name = {option.name}/>)
    showFilter () {

    }
    async checkOption () {
        var option
        if (this.props.id === 1) 
            option = Data.productTypeData
        else if (this.props.id === 2)
            option = Data.themeTypeData
        else if (this.props.id === 3)
            option = Data.neighborhoodTypeData
        else if(this.props.id === 4)
            option = Data.forwhomTypeData
        return option
    }
    async showOptions () {
        const optionComponents = await this.checkOption()

        return optionComponents.map(option => <Option key = {option.id} name = {option.name}/>)

    }
    render() {
    return (  
        <div className={"dropdown"}>
            <button className={"btn btn-primary dropdown-toggle"} type="button" data-toggle="dropdown">{this.props.name}
                <span className="caret"></span>
            </button>
            <ul className={"dropdown-menu"}>
                <input className={"form-control"} id="myInput" type="text" placeholder="Search.." />
                {this.showOptions()}
            </ul>
        </div>

    )
    }
}


export default Dropdown;

