import React from 'react';
import { Redirect } from 'react-router-dom'
// import ReactDOM from 'react-dom';
// import * as serviceWorker from './serviceWorker';

class Search extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            searchtext: "",
            redirect: false
        };
        this.handleChange = this.handleChange.bind(this);
    }
    handleChange(event) {
        this.setState({searchtext: event.target.value})
    }
    renderRedirect = () => {
        if (this.state.redirect) {
            return (
                <Redirect to='/' />
            )
        }
    }
    handleClick =()=> {
        this.setState({redirect: true})
    }

    refreshPage() {
        window.location.reload(false);
      }
    // handleClick =(pt)=> {
    //     this.setState({redirect: true, path: pt})
    // }
    // handleChange(event) {
    //     this.setState({searchtext: event.target.value})
    // }
    // renderRedirect = () => {
    //     if (this.state.redirect && this.state.path === 1) {
    //         return (
    //             <Redirect to='/view1' />
    //         )
    //     }
    //     else if (this.state.redirect && this.state.path === 0) {
    //         return (
    //             <Redirect to='/' />
    //         )
    //     }
    // }


    render() {
    const search_style = {
        width: "30%",
        display: "inline-block",
        marginTop: "0%",
        marginBottom: "0%"
    }
    return (
        <div>
        <div className="jumbotron text-center" style={{marginBottom: "0%"}}>
            <h1>Souvenir Guidebook</h1>
            <div>
            <input type="text" onChange = {this.handleChange} placeholder="anything in your mind...." aria-label="Search" className="form-control" style= {search_style}/>
            &nbsp;&nbsp;&nbsp;&nbsp;
            <button type="button" onClick={()=>this.props.changeSearch(this.state.searchtext)} className="btn btn-success" style={{marginTop: "0%", marginBottom: "0%"}}>search</button>
            </div>
        </div>
        <div >
            {this.renderRedirect()}
            <nav className="navbar navbar-light" style={{backgroundColor: "#eee"}}>
                <div>
                <button className="btn btn-light" onClick={()=>this.handleClick()} style={{backgroundColor: "#eee"}} >
                <span className="oi oi-home"></span>&nbsp;&nbsp;Main Portal
                </button>
                <span className="oi oi-caret-right" ></span>
                <button className="btn btn-light" onClick={refreshPage} style={{backgroundColor: "#eee"}}>
                    Result Page
                </button>
                </div>
                </nav>
        </div>
        </div>  
    )
    }
}


export default Search;

