import React from 'react';
import Item from '../item/item.js'
// import ReactDOM from 'react-dom';
// import * as serviceWorker from './serviceWorker';

class List extends React.Component {
    constructor() {
        super()
        this.state = {
            listData: {}
        }
        this.fetchItems()
    }
    fetchItems = async() => {
        const data = await fetch("http://localhost:3001/stores")
        const stores = await data.json()
        this.setState({
            listData: stores.data.map(item => <Item key = {item.StoreID} storename = {item.StoreName}/>)
        })
    }

    render() {
    return (  
            <div className={"col-sm-3 col-md-6 col-lg-4"}>
                <div className="container-fluid">
                        {this.state.listData}
                </div>
            </div>
    )
    }
}


export default List;

