import React from 'react';

// import ReactDOM from 'react-dom';
// import * as serviceWorker from './serviceWorker';

class Search extends React.Component {
    constructor() {
        super()
        this.state = {searchtext: ''};
        this.handleChange = this.handleChange.bind(this);
    }
    handleChange(event) {
        this.setState({searchtext: event.target.value})
        console.log(this.state.searchtext)
    }
    handleClick(event) {
        console.log(this.state.searchtext);
    }
    render() {
    const search_style = {
        width: "60%",
        display: "inline-block"
    }
    return (  
        <div className={"jumbotron text-center"}>
            <h1>Souvenir Guidebook</h1>
            <p>Type Anything in Your Mind!</p> 
            <input type="text" value={this.state.searchtext} onChange = {this.handleChange} className="form-control" style= {search_style}/>
            <button type="button" onClick={this.handleClick} className={"btn btn-success"}>search</button>
        </div>
    )
    }
}


export default Search;

