class List extends React.Component {
    listData = this.getData()
    itemComponents = listData.map(item => <Item key = {item.id}/>)

    async getData () {
        const data = await fetch("http://localhost:3001/stores")

        const listData = await data.json()
        return listData
    }
    render() {
    return (  
            <div className={"col-sm-3 col-md-6 col-lg-4"}>
                <div className="container-fluid">
                        <Item />
                </div>
            </div>
    )
    }
}


