import React from 'react';
import StoreDetail from '../storedetail/storedetail.js'

// import ReactDOM from 'react-dom';
// import * as serviceWorker from './serviceWorker';

class View2 extends React.Component {
    constructor () {
        super()
        this.state = {
            items: [],
            isLoaded: false
        }

    }
    componentDidMount() {
        fetch("http://localhost:3001/stores/" + this.props.location.state.id)
        .then(res => res.json())
        .then(json => {
            this.setState({
                isLoaded: true,
                items: json
            })
        })
    }
    render() {
        const view2style =  {
            marginLeft: "10%",
            marginRight: "10%"
        }
        var { isLoaded, items } = this.state
        if (!isLoaded) {
            return <div>Loading...</div>
        }
        else {
        return (
            <div>
                <div className="jumbotron text-center" style={{marginBottom: "0%", height: "150px"}}>
                <h1 style={{marginTop: "0px"}}>Souvenir Guidebook</h1>
                </div><br></br>

                <h1 className="display-1" style={{marginLeft: "15%"}}>{items.data.StoreName}</h1>
                <div className="container-fluid" style = {view2style} >
                    <StoreDetail detail = {items.data} />

                </div>
            </div>


        )
        }

    }
}


export default View2;

