import React from 'react';
import Item from '../item/item.js'
// import ReactDOM from 'react-dom';
// import * as serviceWorker from './serviceWorker';

class List extends React.Component {
    // stores.data.map(item => <Item key = {item.StoreID} storename = {item.StoreName}/>)
    constructor () {
        super()
        this.listComponents = this.fetchItems()
    }
    fetchItems = async() => {
        const data = await fetch("http://localhost:3001/stores")
        const stores = await data.json()
        console.log(stores)
        return stores.map(item => <Item key = {item.data.StoreID} storename = {item.data.StoreName}/>)
    }

    render() {
    return (  
            <div className={"col-sm-3 col-md-6 col-lg-4"}>
                <div className="container-fluid">
                        {this.listComponents}
                </div>
            </div>
    )
    }
}


export default List;

